div.script:
$("#showAll").click( function(e) {
  e.stopPropagation();
  $(".collapse").addClass('show');
  $("#showAll").addClass('d-none');
  $("#hideAll").removeClass('d-none');
  return false;
});
$("#hideAll").click( function(e) {
  e.stopPropagation();
  $(".collapse").removeClass('show');
  $("#hideAll").addClass('d-none');
  $("#showAll").removeClass('d-none');
  return false;
});
$("#showList").click( function(e) {
  e.stopPropagation();
  $(".tiles-gallery .card-columns").addClass('full-width');
  $("#showList").addClass('d-none');
  $("#showTiles").removeClass('d-none');
  return false;
});
$("#showTiles").click( function(e) {
  e.stopPropagation();
  $(".tiles-gallery .card-columns").removeClass('full-width');
  $("#showList").removeClass('d-none');
  $("#showTiles").addClass('d-none');
  return false;
});
